let start = startofday(ago(28d));
let end   = startofday(now());
let reportTable = RawEventsTraces
| where timestamp between (start .. end)
| where operation_Name == 'java/appcat/report'
| distinct operation_Id;
RawEventsTraces
| where timestamp between (start .. end)
| where operation_Name == 'java/appcat/command'
| where operation_Id in (reportTable)
| extend data = parse_json(message)
| extend command = tostring(data.message)
| where command == 'analyze'
| extend dimensions = parse_json(customDimensions)
| extend appcatVersion = tostring(dimensions.appcatversion)
| where appcatVersion != 'latest'
| extend status = tostring(dimensions.status)
| where status == 'end'
| extend val = tolong(dimensions.duration)
| extend DurationsBucket = case (
    val > 3600000,
    "> 1hr",
    val > 1800000 and val <= 3600000,
    "30-60 mins",
    val > 900000 and val <= 1800000,
    "15-30 mins",
    val > 600000 and val <= 900000 ,
    "10-15 mins",
    val > 300000 and val <= 600000 ,
    "5-10 mins",
    val > 300000 and val <= 480000 ,
    "5-8 mins",
    val > 180000 and val <= 300000 ,
    "3-5 mins",
    val > 120000 and val <= 180000 ,
    "2 mins",
    val > 60000 and val <= 120000,
    "1 min",
    val > 30000 and val <= 60000,
    "30-60 seconds",
    "less than 30 seconds"
  )
| summarize Runs=count() by DurationsBucket
| order by Runs desc
| render list

